create table sakila.actor (actor_id smallint not null, first_name varchar(45) not null, last_name varchar(45) not null, last_update timestamp default CURRENT_TIMESTAMP not null, constraint pk_actor primary key (actor_id));
create index idx_actor_last_name on sakila.actor (last_name);
create table sakila.actor_genre (actor_genre_id smallint not null, actor_id smallint not null, name varchar(20) not null, constraint pk_actor_genre primary key (actor_genre_id));
create index actor_id on sakila.actor_genre (actor_id);
create table sakila.actor_info (actor_id smallint default 0 not null, first_name varchar(45) not null, last_name varchar(45) not null, film_info clob, constraint pk_actor_info primary key (actor_id, first_name, last_name));
create table sakila.address (address_id smallint not null, address varchar(50) not null, address2 varchar(50), district varchar(20), city_id smallint not null, postal_code varchar(10), phone varchar(20), last_update timestamp default CURRENT_TIMESTAMP not null, constraint pk_address primary key (address_id));
create index idx_fk_city_id on sakila.address (city_id);
create table sakila.category (category_id smallint not null, name varchar(25) not null, last_update timestamp default CURRENT_TIMESTAMP not null, constraint pk_category primary key (category_id));
create table sakila.city (city_id smallint not null, city varchar(50) not null, country_id smallint not null, last_update timestamp default CURRENT_TIMESTAMP not null, constraint pk_city primary key (city_id));
create index idx_fk_country_id on sakila.city (country_id);
create table sakila.country (country_id smallint not null, country varchar(50) not null, last_update timestamp default CURRENT_TIMESTAMP not null, constraint pk_country primary key (country_id));
create table sakila.customer (customer_id smallint not null, store_id smallint not null, first_name varchar(45) not null, last_name varchar(45) not null, email varchar(50), address_id smallint not null, active smallint default 1 not null, create_date timestamp not null, last_update timestamp default CURRENT_TIMESTAMP not null, constraint pk_customer primary key (customer_id));
create index idx_fk_address_id on sakila.customer (address_id);
create index idx_customer_fk_store_id on sakila.customer (store_id);
create index idx_last_name on sakila.customer (last_name);
create table sakila.datetime (id smallint not null, time time, timestamp timestamp, date date, datetime timestamp, constraint pk_datetime primary key (id, time, timestamp, date, datetime));
create table sakila.film (film_id smallint not null, title varchar(255) not null, description clob, release_year date, language_id smallint not null, original_language_id smallint, rental_duration smallint default 3 not null, rental_rate decimal(10,2) default 4.99 not null, length smallint, replacement_cost decimal(10,2) default 19.99 not null, rating varchar(255) default 'G', special_features varchar(255), last_update timestamp default CURRENT_TIMESTAMP not null, constraint pk_film primary key (film_id));
create index idx_fk_language_id on sakila.film (language_id);
create index idx_fk_original_language_id on sakila.film (original_language_id);
create index idx_title on sakila.film (title);
create table sakila.film_actor (actor_id smallint not null, film_id smallint not null, last_update timestamp default CURRENT_TIMESTAMP not null, constraint pk_film_actor primary key (actor_id, film_id));
create index idx_film_actor_fk_film_id on sakila.film_actor (film_id);
create table sakila.film_category (film_id smallint not null, category_id smallint not null, last_update timestamp default CURRENT_TIMESTAMP not null, constraint pk_film_category primary key (film_id, category_id));
create index fk_film_category_category on sakila.film_category (category_id);
create table sakila.film_comment (film_id smallint not null, comment varchar(128), constraint pk_film_comment primary key (film_id, comment));
create index film_comment_film_id on sakila.film_comment (film_id);
create table sakila.film_genre (film_id smallint not null, genre varchar(128), constraint pk_film_genre primary key (film_id, genre));
create index film_genre_film_id on sakila.film_genre (film_id);
create table sakila.film_image (film_id smallint not null, image blob, constraint pk_film_image primary key (film_id));
create index film_image_film_id on sakila.film_image (film_id);
create table sakila.film_origin (film_id smallint not null, city varchar(128), country varchar(128), constraint pk_film_origin primary key (film_id, city, country));
create index film_origin_film_id on sakila.film_origin (film_id);
create table sakila.film_rating (film_rating_id smallint not null, film_id smallint not null, stars smallint not null, constraint pk_film_rating primary key (film_rating_id));
create index film_rating_film_id on sakila.film_rating (film_id);
create table sakila.film_text (film_id smallint not null, title varchar(255) not null, description clob, constraint pk_film_text primary key (film_id));
create index idx_title_description on sakila.film_text (title);
create table sakila.inventory (inventory_id smallint not null, film_id smallint not null, store_id smallint not null, last_update timestamp default CURRENT_TIMESTAMP not null, constraint pk_inventory primary key (inventory_id));
create index idx_inventory_fk_film_id on sakila.inventory (film_id);
create index idx_store_store_id on sakila.inventory (store_id);
create index idx_store_film_id on sakila.inventory (film_id);
create table sakila.language (language_id smallint not null, name varchar(20) not null, last_update timestamp default CURRENT_TIMESTAMP not null, constraint pk_language primary key (language_id));
create table sakila.payment (payment_id smallint not null, customer_id smallint not null, staff_id smallint not null, rental_id integer, amount decimal(10,2) not null, payment_date timestamp not null, last_update timestamp default CURRENT_TIMESTAMP not null, constraint pk_payment primary key (payment_id));
create index fk_payment_rental on sakila.payment (rental_id);
create index idx_payment_fk_customer_id on sakila.payment (customer_id);
create index idx_payment_fk_staff_id on sakila.payment (staff_id);
create table sakila.rental (rental_id integer not null, rental_date timestamp not null, inventory_id smallint not null, customer_id smallint not null, return_date timestamp, staff_id smallint not null, last_update timestamp default CURRENT_TIMESTAMP not null, constraint pk_rental primary key (rental_id));
create index rental_rental_date on sakila.rental (rental_date);
create index rental_inventory_id on sakila.rental (inventory_id);
create index rental_customer_id on sakila.rental (customer_id);
create index idx_rental_fk_customer_id on sakila.rental (customer_id);
create index idx_fk_inventory_id on sakila.rental (inventory_id);
create index idx_rental_fk_staff_id on sakila.rental (staff_id);
create table sakila.staff (staff_id smallint not null, first_name varchar(45) not null, last_name varchar(45) not null, address_id smallint not null, picture blob, email varchar(50), store_id smallint not null, active smallint default 1 not null, username varchar(16) not null, password varchar(40), last_update timestamp default CURRENT_TIMESTAMP not null, constraint pk_staff primary key (staff_id));
create index idx_staff_fk_address_id on sakila.staff (address_id);
create index idx_staff_fk_store_id on sakila.staff (store_id);
create table sakila.store (store_id smallint not null, manager_staff_id smallint not null, address_id smallint not null, last_update timestamp default CURRENT_TIMESTAMP not null, constraint pk_store primary key (store_id));
create index idx_unique_manager on sakila.store (manager_staff_id);
create index idx_store_fk_address_id on sakila.store (address_id);
